
public class IncrementDecrement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		var a=10;
		System.out.println(a);
		a++;
		System.out.println(a);
		++a;
		System.out.println(a);
		
		var b=10;
		System.out.println(b);
		b--;
		System.out.println(b);
		--b;
		System.out.println(b);
	}
	

}
